import React from "react";
import Form from "../../Form/Form";
import FormButton from "../../Form/FormButton/FormButton";
import FormTextarea from "../../Form/FormTextarea/FormTextarea";
import style from '../../Form/Form.module.scss';

class Feedback extends React.Component{
  state = {
	value:''
  }
render() {
  return(
	<div className="wrapper">
	<h1 className="h1 mb-20">Обратная связь</h1>
	  {this.state.value ? <p>{this.state.value}</p> : ''}
	  <Form formOnSubmit = {this.submitFormHandler}>
		<div className={style.form__item}>
		  <FormTextarea textAreaRows={5} textAreaCallback = {this.handleChange}/>
		</div>
		<div className={style.form__item}>
		<FormButton btnText={'Отправить'} btnClass={'btn_pink'} />
		</div>
	  </Form>
	</div>
  )
}
handleChange = (event) => {
  this.setState({
	value: event.target.value
  })
}
submitFormHandler = (event) => {
  event.preventDefault();
  console.info('Form Stop')
}
}
export default Feedback;
