import React from 'react';
import {BrowserRouter as Router, Switch, Route} from "react-router-dom";

import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import Home from "./components/Page/PageHome/Home";
import Portfolio from "./components/Page/PagePortfolio/Portfolio";
import Feedback from "./components/Page/PageFeedback/Feedback";
import Error404 from "./components/Error/Error404";
import Services from "./components/Services/Services";
import PageLogin from './components/Page/PageLogin/PageLogin'
import Admin from "./components/Admin/Admin";
import AdminPageMenu from "./components/Admin/AdminPage/AdminPageMenu/AdminPageMenu";


class App extends React.Component{


    render() {
    const {state} = this.props;


    return (
      <Router>
        <Header menuData={state.menu} />
        <main id="main">
        <Switch>
           <Route exact path='/'>
             <Home articleData={state.articlePage.articles}/>
           </Route>
            <Route path='/portfolio'>
              <Portfolio articleData={state.articlePage.articles}/>
            </Route>
            <Route path='/feedback' component={Feedback}/>
            <Route path='/services'>
              <Services servicesData={state.servicePage.services}/>
            </Route>
          <Route path='/login' component={PageLogin}/>

          {/*<Route exact path='/admin'>*/}
          {/*  <Admin {...this.props} data={state}/>*/}
          {/*</Route>*/}
          <Route path='/admin' render/>
          <Route path="/admin/menu" component={AdminPageMenu} />

            <Route path="*" component={Error404} />
        </Switch>
        </main>
        <Footer/>
      </Router>
    );
  }
}

export default App;
