import React from 'react';
import menuStyle from './Menu.module.scss';
import {NavLink} from "react-router-dom";

class Menu extends React.Component {

    render() {

        const {menu, menuState} = this.props;

        let menuLists = menu.map((menuList) =>
          <li key={menuList.id} className={menuStyle.menu__item}>
              <NavLink activeClassName="selected" to={menuList.url} className={menuStyle.menu__link}>{menuList.name}</NavLink>
          </li>
        );
        return(<nav style={{display:menuState ? 'block':'none' }} className={menuStyle.menuWrap}><ul className="d-flex">{menuLists}</ul></nav>)
    }
}

export default Menu;
