import React from 'react';
import style from '../FormButton/FormButton.module.scss'

class FormButton extends React.Component{
  render() {
    const {btnText, btnClass, onClick} = this.props;

    let btnClassConver ='';

    switch (btnClass) {
	  case 'btn_pink':
	    btnClassConver = style.btn_pink;
	    break;
	  case 'btn_blue':
		btnClassConver = style.btn_blue;
		break;
	  default:
		btnClassConver = style.btn_blue;
	}

	console.log(this.props.children)

    return (
	  btnText ? <button onClick={onClick} className={`${style.btn} ${btnClassConver}`}>{btnText}</button> : ''
	)
  }
}
export default FormButton;
