import React from 'react';
import style from './AdminPagePortfolio.module.scss'
import Form from "../../../Form/Form";
import FormInput from "../../../Form/FormInput/FormInput";
import styleForm from "../../../Form/Form.module.scss";
import FormButton from "../../../Form/FormButton/FormButton";
import FormTextarea from "../../../Form/FormTextarea/FormTextarea";
import {ADMIN} from "../../../../lang";
import {updatePortfolioCreator} from "../../../../redux/portfolio-reducer";

const AdminPagePortfolio = props => {
  const {portfilioData, portfilioDataNew} = props;



  const portfilios = portfilioData.map(portfilio =>
  <li className='mb-20 portfilio__item' key={portfilio.id}>
	<div className='h4'>{portfilio.title}</div>
	<div className={style.portfilioItem}>{portfilio.text}</div>
	<div>Колличество комментариев: {portfilio.comments !== undefined ? portfilio.comments.length : 0}</div>
  </li>)

  const handleForm = (evt) => {
    evt.preventDefault();
  }
  const handleChangeInput = (evt) => {
	 props.dispatch(updatePortfolioCreator(evt.target.name, evt.target.value))
  }

  return(
	<React.Fragment>
	  <h1 className="h1">Редактировать портфолио</h1>

	  <ul className={style.wrap}>{portfilios}</ul>
	  <h2 className='h3 mb-20'>Добавить новый проект</h2>
	  <div className={style.add}>
	  <Form formOnSubmit={handleForm}>
		<div className={styleForm.form__item}>
		<FormInput name='title' onChange={handleChangeInput} required placeholder='Название проекта' value={portfilioDataNew.title}/>
		</div>
		<div className={styleForm.form__item}>
		  <FormTextarea onChange={handleChangeInput} placeholder='Описание проекта'>{portfilioDataNew.text}</FormTextarea>
		</div>
		<div className={styleForm.form__item}>
		  <FormButton>{ADMIN.add}</FormButton>
		</div>
	  </Form>
	  </div>

	</React.Fragment>
  )
}
export default AdminPagePortfolio;
