import Header from './components/Header/Header';
import articleData from './components/Article/ArticleData';
import ArticleList from "./components/Article/ArticleList";

class App extends React.Component{
  constructor(props) {
    super(props)

    this.state = {
      reverted: false
    }
  }
  articleData = articleData.slice();

  render() {
    console.log('---', 2, this.state)
    return (
      <>
      <Header />
      <main>
          <div className="wrapper">
            <div className="d-flex align-self-center">
              <h1 className="h1">Последние новости</h1>
              <button onClick={this.handleRevert} className="ml-auto">Изменить последовательность</button>
            </div>

            <div className="article-wrap">
              <ArticleList articles={articleData}/>
            </div>
          </div>
      </main>
      </>
    );
  }
  handleRevert = () =>  {
    console.log('---', 1);
    articleData.reverse();
    this.setState( {
      reverted: !this.state.reverted
    })
  }

}

export default App;
