  render() {

	const {articleItem, isOpen, onButtonClick} = this.props;

	return(
		<div className={style.a} onClick={this.incrementClick}>
		  <div className="article-list__item-title">{articleItem.title}</div>
		  <button aria-label="Закрыть" onClick={onButtonClick}>{isOpen ? 'Скрыть': 'Показать'} описание проека</button>
		  <p className={'article-list__item-text'} style={{display: isOpen ? 'block' : 'none'}}>{articleItem.text}</p>
		  <div className="article-list__item-date">{new Date(articleItem.date).toDateString()}</div>
		  <p>Колличество кликов: {this.state.count}</p>
		</div>
	)
  }
incrementClick = () => {
  this.setState({
	count: this.state.count + 1
  })
}


}

export default Article;
