import React from 'react';


class Article extends React.Component {
  constructor(props) {
    super(props)

	this.state = {
      isOpen:true
	}
	this.handelClick = handelClick.bind(this);
  }
  render() {

    const articlesData = this.props.articles;
    let toggle = {
      display: this.state.isOpen ? 'block' : 'none'
	};

	console.log(this.state.isOpen);

    let articles = articlesData.map((article) =>
      <li key={article.id} className="article-list__item">
		<div className="article-list__item-wrap">
		  <button aria-label="Закрыть" onClick={this.handelClick}>&times;</button>
		  <div className="article-list__item-title">{article.title}</div>
		   <p className={'article-list__item-text'} style={toggle}>{article.text}</p>
		</div>
      </li>

	);

	return(
      <ul className="article-list">{articles}</ul>
	)
  }
}
function handelClick() {
  console.log('---', 'clicked');
  this.setState({
	isOpen: !this.state.isOpen
  });
}
export default Article;
