import React from 'react';
import style from '../Form.module.scss'

class FormInput extends React.Component {

  componentDidMount() {
	this.textInput.current.focusTextInput();
  }

  render() {
	const {name, placeholder, type = 'text', required = false, onChange} = this.props;
    return(
	<input type={type} name={name} required={required} placeholder={placeholder} className={style.formControl} onChange={onChange}/>
	)
  }
}
export default FormInput;
