import React from 'react'
import style from "../../Admin/Admin.module.scss";
import AdminMenu from "../../Admin/AdminMenu/AdminMenu";

class LayoutAdmin extends React.Component{
  render() {
	const {sidebar} = this.props;
    return (
	  <div className="wrapper">
		<h1 className="h1">Админка</h1>
	 <div className="d-flex mt-40">
		<aside className={style.asideMenu}>
		  <AdminMenu menu={sidebar}/>
		</aside>
	    {this.props.children}
	  </div>
	  </div>
	)
  }
}
export default LayoutAdmin
