const UPDATE_SERVICE = 'UPDATE_PORTFOLIO';


const portfolioReducer = (state, action) => {
  switch (action.type) {
	case UPDATE_SERVICE:
	  return state;
	default:
	  return state;
  }
}
export const updatePortfolioCreater = (title, value) => {
  ({type:UPDATE_SERVICE, title:title, value:value})

}

export default portfolioReducer
