import React from 'react';
import style from '../Article/Article.module.scss';


class Article extends React.PureComponent {
  render() {

	const {articleItem, isOpen} = this.props;

	return(
		<div className={style.articleList__itemWrap}>
		  <div className={style.articleList__itemTitle}>{articleItem.title}</div>
		  <div><img src="{articleItem.img}" alt=""/></div>
		  <p className={style.articleList__itemText} style={{display: isOpen ? 'block' : 'none'}}>{articleItem.text}</p>
		  <div className={style.articleList__itemDate}>{new Date(articleItem.date).toDateString()}</div>
		</div>
	)
  }


}

export default Article;
