}
}

const feedbackReducer = (state = initialState, action) => {
  switch (action.type) {
	case FEEDBACK_INPUT:
	  state.newFeedback.text = action.v
	  console.log(state,stat)
	  return state;
	case FEEDBACK_SEND:
