import React from 'react';
import Menu from '../Menu/Menu'
import menus_data from './navdata';

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen:true
    }
  }

  render () {
  return (
      <header className="header">
        <div className="wrapper">
          <div className="d-flex">
		  <Menu menu={menus_data} />
            <button className={`header__burger ${this.state.isOpen ? '':'header__burger_active'}`} onClick={this.menuState}>&times;</button>
          </div>
        </div>
        </header>
  )
}
menuState = () => {
  this.setState ({
      isOpen: !this.state.isOpen
    })
}
}
export default Header
