import React from 'react';
import AdminPagePortfolio from "./AdminPagePortfolio";
import {updatePortfolioCreator, newPortfolioCreator} from "../../../../redux/portfolio-reducer";

const AdminPagePortfolioContainer = props => {
  const store = props.store.getState();


  const handleChangeInput = evt => props.dispatch(updatePortfolioCreator(evt.target.name, evt.target.value))

  const handleOnSubmit = () => props.dispatch(newPortfolioCreator());

  console.log(store);

  return(
    <AdminPagePortfolio portfolioData={store.portfolioPage.portfolio}
						portfolioDataNew={store.portfolioPage.newPortfolio}
						portfolioEdit={handleChangeInput}
						portfolioAdd={handleOnSubmit}/>
  )
}
export default AdminPagePortfolioContainer;
