import React from 'react';
import ArticleList from "../Article/ArticleList/ArticleList";


class Home extends React.Component {
  state = {
	  reverted: false,
	  value: ''
  }
  render() {
    console.log(this.state)
	const {articleData} = this.props;
    return(
	  <div className="wrapper">
		<form action="">
		<textarea value={this.state.value} onChange={this.handleChange}/>
		</form>
		<div className="d-flex align-self-center">
		  <h1 className="h1">Последние работы</h1>
		  <button onClick={this.handleRevert} className="ml-auto">Изменить последовательность</button>
		</div>
		<div className="article-wrap">
		  <ArticleList articles={this.state.reverted ? articleData.slice().reverse() : articleData}/>
		</div>
	  </div>
	)
  }
  handleRevert = () => {
    this.setState({
	  reverted: !this.state.reverted
	})
  }
  handleChange = (event) => {
    console.log(event.target.value);
    this.setState({
	 //value: event.target.value
	})
  }
}
export default Home;
