import React from 'react';


class Article extends React.Component {
  constructor(props) {
	super(props)

	this.state = {
	  isOpen:props.defaultOpen
	}
	this.handelClick = handelClick.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    if(nextProps.defaultOpen !== this.props.defaultOpen) {
       this.setState({
		 isOpen:nextProps.defaultOpen
	   })
	}
  }
  componentDidUpdate() {
    console.log('----','componentDidUpdate' )
  }

  render() {

	const {articleItem} = this.props;

	return(
		<div className="article-list__item-wrap">
		  <div className="article-list__item-title">{articleItem.title}</div>
		  <button aria-label="Закрыть" onClick={this.handelClick}>{this.state.isOpen ? 'Скрыть': 'Показать'} описание проека</button>
		  <p className={'article-list__item-text'} style={{display: this.state.isOpen ? 'block' : 'none'}}>{articleItem.text}</p>
		  <div className="article-list__item-date">{new Date(articleItem.date).toDateString()}</div>
		</div>
	)
  }
}
function handelClick() {
  this.setState({
	isOpen: !this.state.isOpen
  });
}
export default Article;
