import React from 'react';
import ReactDOM from 'react-dom';
import Form from "../../../Form/Form";
import FormInput from "../../../Form/FormInput/FormInput";
import FormButton from "../../../Form/FormButton/FormButton";

import style from './AdminPageMenu.module.scss';
import styleForm from "../../../Form/Form.module.scss";

class AdminPageMenu extends React.Component{
  state = {
    name: '',
	url:''
  };


  handleForm = (evt) => {
	evt.preventDefault();
	this.props.addMenu(this.state.name, this.state.url);

	const [input] = evt.target.children;
	console.log(input)


  }
  // handleInputChange = (evt) => {
	// const name = evt.target.name;
	// const value = evt.target.value;
	//
	// this.setState({
	//   [name]:value
	// });
  // }


  getInputRef = (node) => {
    this._inputEl = node;
  }

  render() {
	const {menuData} = this.props;



	const menuLists = menuData.map(menuList =>
	 <li className="mb-20" key={menuList.id}>
	   <div className="mb-5"><b>ID:</b> {menuList.id}</div>
	   <div className="mb-5"><b>Имя:</b> {menuList.name}</div>
	   <div className="mb-5"><b>URL:</b> {menuList.url}</div>
	 </li>
	)

    return(
      <React.Fragment>
		<h1 className="h1">Редактировать меню</h1>
	    <ul className={style.wrap}>{menuLists}</ul>
		<div className={style.add}>
		<h3 className="h3 mb-20">Добавить URl</h3>
		<Form formOnSubmit={this.handleForm}>
		  {this.state.name}
		  <div className={styleForm.form__item}>
			<FormInput required name="name" value={this.state.name} placeholder="Введите название URL" ref={this.getInputRef}/>
		  </div>
		  {this.state.url}
		  <div className={styleForm.form__item}>
			<FormInput name="url" required placeholder="Введите URL" ref={this.getInputRef}/>
		  </div>
		 <div className={styleForm.form__item}>
		   <FormButton btnText="Добавить URL" btnClass="btn_blue"/>
		 </div>
		</Form>
		</div>
	  </React.Fragment>
	)
  }
}
export default AdminPageMenu;
