import React from 'react';
import {addMenuCreator, updateMenuCreator} from "../../../../redux/menu-reducer";
import AdminPageMenu from "./AdminPageMenu";


class AdminPageMenuConteiner extends React.Component {

  state = {
	edit: false
  }

  handleSubmit = evt => {
	evt.preventDefault();
	this.props.store.dispatch(addMenuCreator());
  }
  handleSubmitItem = evt => {
    evt.preventDefault();
    this.setState({
	  edit: false
	})
  }
  handleInputChange = (name, value, id) => {

	this.props.store.dispatch(updateMenuCreator(name, value, id));
  }

  handleEditMenuItem = idMenu => {
	this.setState({edit: idMenu});
  }


  render() {

	console.log(this.props.dispatch);

	return (
	  <AdminPageMenu menuData={this.props.menuData} menuDataNew={this.props.menuDataNew} updateMenu={this.handleInputChange} addMenu={this.handleSubmit}/>
	)
  }
}

export default AdminPageMenuConteiner;
