const UPDATE_PORTFOLIO = 'UPDATE_PORTFOLIO',
      NEW_PORTFOLIO = 'NEW_PORTFOLIO';


const portfolioReducer = (state, action) => {
  switch (action.type) {
	case NEW_PORTFOLIO:

	  const newPortfolioItem = {
	    id: '',
		title: state.newPortfolio.title,
		text: state.newPortfolio.text,
	  }

	  state.portfolio.push(newPortfolioItem);

	  console.log(444)

	return state;

	case UPDATE_PORTFOLIO:

	  state.newPortfolio[action.name] = action.value;

	  return state;
	default:
	  return state;
  }
}
export const newPortfolioCreator = () => ({type: NEW_PORTFOLIO});
export const updatePortfolioCreator = (name, value) => ({type:UPDATE_PORTFOLIO, name:name, value:value});

export default portfolioReducer
