import React from 'react';
import {NavLink} from "react-router-dom";
import style from './AdminMenu.module.scss'

class AdminMenu extends React.Component {
  render() {
	const {menu} = this.props;

	const menus = menu.map((menuItem) =>
		<li key={menuItem.id} className={style.menu__item}>
		  <NavLink exact to={`/admin/${menuItem.url}`} className={style.menu__link} activeClassName="selected">{menuItem.name}</NavLink>
		</li>
	)

    return(
      <ul className={style.menu}>{menus}</ul>
	)
  }
}
export default AdminMenu;
