import React from 'react';
import './_Menu.scss';

export default class Menu extends React.Component {
constructor(props) {
    super(props)
    this.state = {
        isOpen:false
    }

}
render() {

    const menusData = this.props.menu;


    let menuLists = menusData.map((menuList) =>
     <li key={menuList.id} className="menu__item"><a href={menuList.url} className={"menu_link"}>{menuList.name}</a></li>
    )

    return(
      <ul className="menu d-flex">{menuLists}</ul>
    )
}
}
