const UPDATE_MENU = 'UPDATE-MENU',
  ADD_MENU = 'ADD-MENU',
  ADD_MENU_ITEM = 'ADD-MENU-ITEM',
  UPDATE_MENU_STATUS = 'UPDATE-MENU-STATUS';

const initialState = {
  menuList: [
	{
	  "id": 1,
	  "name": "Главная",
	  "url": "/"
	},
	{
	  "id": 2,
	  "name": "Портфолио",
	  "url": "/portfolio"
	},
	{
	  "id": 3,
	  "name": "Обратная связь",
	  "url": "/feedback"
	},
	{
	  "id": 4,
	  "name": "Услуги",
	  "url": "/services"
	},
	{
	  "id": 5,
	  "name": "Войти",
	  "url": "/login"
	}
  ],
  menuNew: {
	"name": "",
	"url": ""
  },
  isOpen: true
}

const menuReducer = (state = initialState, action) => {

  const copyState = {...state, menuList: [...state.menuList]};


  switch (action.type) {
	case ADD_MENU:
	  const newMenu = {
		id: state.menuList.length + 1,
		name: state.menuNew.name,
		url: state.menuNew.url
	  }
	  copyState.menuList.push(newMenu);
	  copyState.menuNew = {name: '', url: ''}
	  return copyState;
	case ADD_MENU_ITEM:
	  return copyState;
	case UPDATE_MENU:

	  if (action.item !== false) {
		copyState.menuList[action.item - 1][action.name] = action.value;
	  } else {
		copyState.menuNew[action.name] = action.value
	  }
	  return copyState;
	case UPDATE_MENU_STATUS:
	  copyState.isOpen = !copyState.isOpen;
	  return copyState;
	default:
	  return state;

  }
}
export const addMenuCreator = () => ({type: ADD_MENU}),
  addMenuItemCreator = () => ({type: ADD_MENU_ITEM}),
  updateMenuCreator = (menuName, menuValue, menuItem) =>
	({type: UPDATE_MENU, name: menuName, value: menuValue, item: menuItem}),
  updateMenuStatus = () => ({type: UPDATE_MENU_STATUS});

export default menuReducer;
