import React from 'react';
import Menu from '../Menu/Menu'
import menus_data from './navdata';

class Header extends React.Component {
  state = {
    isOpen:true
  }
render () {
  return (
      <header className="header">
        <div className="wrapper">
          <div className="d-flex">
		  <Menu menu={menus_data} menuState={this.state.isOpen}/>
           <button className={`burger ${this.state.isOpen ? '':'burger_active'}`} onClick={this.burgerState}
           >&times;</button>
          </div>
        </div>
        </header>
  )
}
burgerState = () => {
  this.setState ({
      isOpen: !this.state.isOpen
    })
}
}
export default Header
