import React from "react";
import {feedbackInputCreator, feedbackSendCreator} from "../../../redux/feedback-reducer";
import Feedback from "./Feedback";


const FeedbackContainer = props => {

  const store = props.store.getState();

  console.log(store);

  const handleInputChange = (name,value) => props.store.dispatch(feedbackInputCreator(name, value));
  const submitFormHandler = () => props.store.dispatch(feedbackSendCreator());

  return(<Feedback
	feedbackNew={store.feedbackPage.newFeedback}
	feedbackEdit={handleInputChange}
	feedbackAdd={submitFormHandler}/>)
}
export default FeedbackContainer;
