const UPDATE_MENU = 'UPDATE-MENU',
      ADD_MENU = 'ADD-MENU';

const menuReduser = (state,action) => {

  switch (action.type) {
	case ADD_MENU:
	  let newMenu = {
		id : state.menuList.length+1,
		name:state.menuNew.name,
		url: state.menuNew.url
	  }
	  state.menuList.push(newMenu);
	  state.menuNew = {name:'', url:''}
	  break;
	case UPDATE_MENU:
	  if(action.item !== false) {
		state.menuList[action.item-1][action.name] = action.value;
	  } else {
		state.menuNew[action.name] = action.value
	  }
	  break;
	default:
	  return state;
  }
}
export default menuReduser
