import React from 'react';
import ReactDOM from 'react-dom';
import Form from "../../../Form/Form";
import FormInput from "../../../Form/FormInput/FormInput";
import FormButton from "../../../Form/FormButton/FormButton";

import style from './AdminPageMenu.module.scss';
import styleForm from "../../../Form/Form.module.scss";

class AdminPageMenu extends React.Component{
  // state = {
  //   menuName: '',
	// menuUrl:''
  // };

  handleSubmit = (evt) => {
	evt.preventDefault();
	const menuName = this.name.value,
	      menuUrl = this.url.value;

	// this.setState({
	//   menuName: menuName,
	//   menuUrl: menuUrl
	// });

	//this.props.addMenu(menuName, menuUrl);
	this.props.updateMenu('u', 'u');

  }
  handleInputChange = evt => {
    console.log(this.name.value)
	const target = evt.target;
	const name = target.name;
	const value = target.value;
	this.props.updateMenu(name, value);
  }

  render() {
	const {menuData, formValue} = this.props;



	const menuLists = menuData.map(menuList =>
	 <li className="mb-20" key={menuList.id}>
	   <div className="mb-5"><b>ID:</b> {menuList.id}</div>
	   <div className="mb-5"><b>Имя:</b> {menuList.name}</div>
	   <div className="mb-5"><b>URL:</b> {menuList.url}</div>
	   <FormButton btnText="Изменить" btnClass="btn_blue"/>
	 </li>
	)

    return(
      <React.Fragment>
		<h1 className="h1">Редактировать меню</h1>
	    <ul className={style.wrap}>{menuLists}</ul>
		<div className={style.add}>
		<h3 className="h3 mb-20">Добавить URl</h3>
		{/*<Form formOnSubmit={this.handleSubmit}>*/}
		{/*  <div className={styleForm.form__item}>*/}
		{/*	<FormInput required*/}
		{/*			   name='menuName'*/}
		{/*			   value={this.state.menuName}*/}
		{/*			   placeholder="Введите название URL"*/}
		{/*			   onChange={this.handleInputChange}*/}
		{/*			   refCallback={input => this.name = input}/>*/}
		{/*  </div>*/}
		{/*  <div className={styleForm.form__item}>*/}
		{/*	<FormInput required*/}
		{/*			   name='menuUrl'*/}
		{/*			   value={this.state.menuUrl}*/}
		{/*			   placeholder="Введите URL"*/}
		{/*			   onChange={this.handleInputChange}*/}
		{/*			   refCallback={input => this.url = input}/>*/}
		{/*  </div>*/}
		{/* <div className={styleForm.form__item}>*/}
		{/*   <FormButton btnText="Добавить URL" btnClass="btn_blue"/>*/}
		{/* </div>*/}
		{/*</Form>*/}
		  <Form formOnSubmit={this.handleSubmit}>
		  <div className={styleForm.form__item}>
			<FormInput required
					   name='name'
					   value={formValue.name}
					   placeholder="Введите название URL"
					   onChange={this.handleInputChange}
					   refCallback={input => this.name = input}/>
		  </div>
		  <div className={styleForm.form__item}>
			<FormInput required
					   name='url'
					   value={formValue.url}
					   placeholder="Введите URL"
					   onChange={this.handleInputChange}
					   refCallback={input => this.url = input}/>
		  </div>
		 <div className={styleForm.form__item}>
		   <FormButton btnText="Добавить URL" btnClass="btn_blue"/>
		 </div>
		</Form>
		</div>
	  </React.Fragment>
	)
  }
}
export default AdminPageMenu;
