const UPDATE_MENU = 'UPDATE-MENU',
      ADD_MENU = 'ADD-MENU',
      UPDATE_MENU_STATUS = 'UPDATE-MENU-STATUS';

const initialState = {
  menuList: [
	{
	  "id":1,
	  "name":"Главная",
	  "url":"/"
	},
	{
	  "id":2,
	  "name":"Портфолио",
	  "url":"/portfolio"
	},
	{
	  "id":3,
	  "name":"Обратная связь",
	  "url":"/feedback"
	},
	{
	  "id":4,
	  "name":"Услуги",
	  "url":"/services"
	},
	{
	  "id":5,
	  "name":"Войти",
	  "url":"/login"
	}
  ],
  menuNew: {
	"name":"",
	"url":""
  },
  isOpen:true
}

const menuReducer = (state = initialState, action) => {
  switch (action.type) {
	case ADD_MENU: {
	  let newMenu = {
		id: state.menuList.length + 1,
		name: state.menuNew.name,
		url: state.menuNew.url
	  }
	  const copyState = {...state};
	  copyState.menuList.push(newMenu);
	  copyState.menuNew = {name: '', url: ''}
	  return copyState;
	}
	case UPDATE_MENU: {
	  const copyState = {...state};
	  copyState.menuNew = {...state.menuNew}
	  if (action.item !== false) {
		copyState.menuList[action.item - 1][action.name] = action.value;
	  } else {
		copyState.menuNew[action.name] = action.value
	  }
	  return copyState;
	}
	case UPDATE_MENU_STATUS: {
	  let stateCopy = {...state};
	  stateCopy.isOpen = !stateCopy.isOpen;
	  return stateCopy;
	}
	default:
	  return state;

  }
}
export const addMenuCreator = () => ({type: ADD_MENU});

export const updateMenuCreator = (menuName, menuValue, menuItem) =>
  ({type: UPDATE_MENU, name: menuName, value: menuValue, item: menuItem});

export const updateMenuStatus = () => ({type: UPDATE_MENU_STATUS});

export default menuReducer;
