import React from 'react';
import Article from '../Article';
import style from '../Article.module.scss';

class ArticleList extends React.PureComponent{
  state = {
	openArticleId:null
}

  render() {
    const articlesData = this.props.articles;

	let articles = articlesData.map( (article) =>
	  <li key={article.id} className={style.articleList__item}>
		<Article articleItem={article}
				 isOpen={this.state.openArticleId === article.id}
				 onButtonClick = {this.handleClick.bind(this, article.id)}
		/>
	  </li>
	)

	return(
	  <ul className={style.articleList}>{articles}</ul>
	)
  }

  handleClick = openArticleId => {
  this.setState({
	openArticleId: this.state.openArticleId === openArticleId ? null : openArticleId
  })
  }
}
export default ArticleList;
