import React from 'react';
import {Route} from "react-router-dom";

import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import articleData from './components/Article/ArticleData';
import ArticleList from "./components/Article/ArticleList";

class App extends React.PureComponent{
  constructor(props) {
    super(props)

    this.state = {
      reverted: false
    }
  }

  render() {
    console.log('---', 2, this.state)
    return (
      <>
      <Header />
      <main>

          <div className="wrapper">
            <div className="d-flex align-self-center">
              <h1 className="h1">Последние работы</h1>
              <button onClick={this.handleRevert} className="ml-auto">Изменить последовательность</button>
            </div>

            <div className="article-wrap">
              <ArticleList articles={this.state.reverted ? articleData.slice().reverse() : articleData}/>
            </div>

          </div>
      </main>
        <Footer/>
      </>
    );
  }
  handleRevert = () =>  {
    console.log('---', 1);
    this.setState( {
      reverted: !this.state.reverted
    })
  }

}

export default App;
