const FEEDBACK_INPUT = 'FEEDBACK_INPUT',
      FEEDBACK_SEND  = 'FEEDBACK_SEND';

const initialState = {
	feedbackArchive: [{
	}],
	newFeedback: {
    text:''
}
}

const feedbackReducer = (state = initialState, action) => {
  switch (action.type) {
	case FEEDBACK_INPUT:
	  state.newFeedback[action.name] = action.value
	  return state;
	case FEEDBACK_SEND:
	  state.newFeedback = {text: ''};
	  return state;
	default:
	  return state;

  }
}

export const feedbackInputCreator = (name, value) => ({type: FEEDBACK_INPUT, name:name, value:value});
export const feedbackSendCreator = () => ({type: FEEDBACK_SEND});

export default feedbackReducer;
