import React from 'react';
import style from './Admin.module.scss'
import AdminMenu from "./AdminMenu/AdminMenu";
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import AdminPageMenu from "./AdminPage/AdminPageMenu/AdminPageMenu";

class Admin extends React.Component{
  render() {
	const {data, match} = this.props;


    return(
	  <div className="wrapper">
		<h1 className="h1">Админка</h1>
		<div className="d-flex mt-40">
	    <aside className={style.asideMenu}>
		  <AdminMenu path={match.path} menu={data.adminSidebar}/>
	    </aside>
		<main>
		  <Router>
			<Switch>
			  <Route path="/admin/menu" component={AdminPageMenu} />
			</Switch>
		  </Router>
		</main>
		</div>
	  </div>
	)
  }
}
export default Admin
