import React from 'react';


class Article extends React.Component {
  constructor(props) {
	super(props)

	this.state = {
	  isOpen:props.defaultOpen,
	  count: 0
	}
  }
  shouldComponentUpdate(nextProps, nextState) {
    return this.state.isOpen !== nextState.isOpen

  }

  componentDidMount() {
    console.log('---', 'componentWillMount')
  }


  componentWillReceiveProps(nextProps) {
	//console.log('---','componentWillReceiveProps' )
    if(nextProps.defaultOpen !== this.props.defaultOpen) {
       this.setState({
		 isOpen:nextProps.defaultOpen
	   })
	}
  }
  componentDidUpdate() {
    console.log('---','componentDidUpdate' )
  }

  render() {

	const {articleItem} = this.props;

	return(
		<div className="article-list__item-wrap" onClick={this.incrementClick}>
		  <div className="article-list__item-title">{articleItem.title}</div>
		  <button aria-label="Закрыть" onClick={this.handelClick}>{this.state.isOpen ? 'Скрыть': 'Показать'} описание проека</button>
		  <p className={'article-list__item-text'} style={{display: this.state.isOpen ? 'block' : 'none'}}>{articleItem.text}</p>
		  <div className="article-list__item-date">{new Date(articleItem.date).toDateString()}</div>
		  <p>Колличество кликов: {this.state.count}</p>
		</div>
	)
  }

handelClick = () => {
	this.setState( {
	  isOpen: !this.state.isOpen
	})
  }
incrementClick = () => {
  this.setState({
	count: this.state.count + 1
  })
}

}

export default Article;
