import React from 'react';
import {BrowserRouter as Router, Switch, Route} from "react-router-dom";

import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import Home from "./components/Page/PageHome/Home";
import Portfolio from "./components/Page/PagePortfolio/Portfolio";
import Feedback from "./components/Page/PageFeedback/Feedback";
import Error404 from "./components/Error/Error404";
import Services from "./components/Services/Services";
import PageLogin from './components/Page/PageLogin/PageLogin'
import Admin from "./components/Admin/Admin";
import LayoutFront from "./components/Layout/LayoutFront/LayoutFront";
import LayoutAdmin from "./components/Layout/LayoutAdmin/LayoutAdmin";
import AdminPageMenuContainer from "./components/Admin/AdminPage/AdminPageMenu/AdminPageMenuContainer";
import AdminPagePortfolioContainer from "./components/Admin/AdminPage/AdminPagePortfolio/AdminPagePortfolioContainer";


const App = props => {
    const {store,state, dispatch} = props;

    return (
      <React.Fragment>
      <Router>
        <Header menuData={store.menu.menuList} />
        <LayoutFront>
          <Switch>
         <Route exact path='/'>
           <Home articleData={state.portfolioPage.portfolio}/>
         </Route>
          <Route path='/portfolio'>
           <Portfolio articleData={state.portfolioPage.portfolio}/>
          </Route>
            <Route path='/feedback'>
              <Feedback newFeedback={state.feedbackPage.newFeedback} dispatch={dispatch}/>
            </Route>
          <Route path='/services'>
           <Services servicesData={state.servicePage.services}/>
          </Route>
          <Route path='/login' component={PageLogin}/>
          </Switch>
        </LayoutFront>
        <Route path='/admin/'>
          <LayoutAdmin sidebar={state.adminPage.adminSidebar}>
            <Switch>
              <Route exact path='/admin'><Admin/></Route>
              <Route path='/admin/menu'><AdminPageMenuContainer store={store}/></Route>
              <Route path='/admin/portfolio'><AdminPagePortfolioContainer store={store}/></Route>
            </Switch>
          </LayoutAdmin>
        </Route>
      </Router>
        <Footer/>
      </React.Fragment>
    )
}

export default App;
