const FEEDBACK_UPDATE = 'FEEDBACK_UPDATE',
      FEEDBACK_SEND  = 'FEEDBACK_SEND';

const initialState = {
	feedbackArchive: [{
	}],
	newFeedback: {
    text:''
}
}

const feedbackReducer = (state = initialState, action) => {
  switch (action.type) {
	case FEEDBACK_UPDATE:
	  const copyState = {...state};
	  copyState.newFeedback = {...copyState.newFeedback}
	  copyState.newFeedback[action.name] = action.value
	  return copyState;
	case FEEDBACK_SEND:
	  state.newFeedback = {text: ''};
	  return state;
	default:
	  return state;

  }
}

export const feedbackInputCreator = (name, value) => ({type: FEEDBACK_UPDATE, name:name, value:value});
export const feedbackSendCreator = () => ({type: FEEDBACK_SEND});

export default feedbackReducer;
