import React from 'react';
import './_Menu.scss';

class Menu extends React.Component {
constructor(props) {
    super(props)
    this.state = {
        isOpen:true
    }
    this.menuState = menuState.bind(this);

}
render() {

    const menusData = this.props.menu;
    let toggle = {
        display: this.state.isOpen ? 'block' : 'none'
    };

    let menuLists = menusData.map((menuList) =>
     <li key={menuList.id} className="menu__item"><a href={menuList.url} className={"menu_link"}>{menuList.name}</a></li>
    )

    return(
      <>
      <nav style={toggle}><ul className="menu d-flex">{menuLists}</ul></nav>
      <button className="header__burger" onClick={this.menuState}>&times;</button>
      </>
    )
    }
}
function menuState() {
    this.setState( {
        isOpen: !this.state.isOpen
    })
}
export default Menu;
