import React from 'react';
import Article from './Article';
import './_Article.scss'

class ArticleList extends React.PureComponent{
  constructor(props) {
    super(props);

  }

  render() {
    const articlesData = this.props.articles;

	let articles = articlesData.map( (articles, index) =>
	  <li key={articles.id} className="article-list__item"><Article articleItem={articles} defaultOpen = {index === 0} /></li>
	)

	return(
	  <ul className="article-list">{articles}</ul>
	)
  }
}
export default ArticleList;
